$("#contactForm").validator().on("submit",function(event){if(event.isDefaultPrevented()){formError();submitMSG(!1,"¿ Completaste el formulario correctamente ?")}else{event.preventDefault();submitForm()}});$("#contactFormModal").validator().on("submit",function(event){if(event.isDefaultPrevented()){formErrorModal();submitMSGModal(!1,"¿ Completaste el formulario correctamente ?")}else{event.preventDefault();submitFormModal()}});function submitForm(){var name=$("#name").val();var email=$("#email").val();var telefono=$("#telefono").val();var message=$("#message").val();var desde=$("#desde").val();$.ajax({type:"POST",url:"php/form-process.php",data:"desde="+desde+"&name="+name+"&email="+email+"&telefono="+telefono+"&message="+message,success:function(text){if(text=="success"){formSuccess()}else{formError();submitMSG(!1,text)}}})}
function submitFormModal(){var name=$("#nameModal").val();var email=$("#emailModal").val();var telefono=$("#telefonoModal").val();var message=$("#messageModal").val();var desde=$("#desde").val();$.ajax({type:"POST",url:"php/form-process.php",data:"desde="+desde+"&name="+name+"&email="+email+"&telefono="+telefono+"&message="+message,success:function(text){if(text=="success"){formSuccessModal()}else{formErrorModal();submitMSGModal(!1,text)}}})}
function formSuccess(){$("#contactForm")[0].reset();submitMSG(!0,"Mensaje Enviado !")}
function formSuccessModal(){$("#contactFormModal")[0].reset();submitMSGModal(!0,"Mensaje Enviado !")}
function formError(){$("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend',function(){$(this).removeClass()})}
function formErrorModal(){$("#contactFormModal").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend',function(){$(this).removeClass()})}
function submitMSG(valid,msg){if(valid){var msgClasses="h3 text-center tada animated text-success"}else{var msgClasses="h3 text-center text-danger"}
$("#msgSubmit").removeClass().addClass(msgClasses).text(msg)}
function submitMSGModal(valid,msg){if(valid){var msgClasses="h3 text-center tada animated text-success"}else{var msgClasses="h3 text-center text-danger"}
$("#msgSubmitModal").removeClass().addClass(msgClasses).text(msg)}